using ClosedXML.Excel;
using GlobalObjects;
using System;
using System.IO;
using System.Collections.Concurrent;
using System.Collections.Generic;
using Diff;

namespace AnalyzeCode
{
    class Analyze
    {
        /// <summary>
        /// 在所有分析前调用
        /// </summary>
        /// <param name="paramDic">传入的参数</param>
        /// <param name="globalObject">全局存在, 可以保存需要在其他调用时使用的数据, 如当前行号等</param>
        /// <param name="allFilePathList">将会分析的所有文件路径列表</param>
        public void RunBeforeAnalyzeSheet(Dictionary<string, string> paramDic, ref Object globalObject, List<string> allFilePathList)
        {
            Logger.Info("RunBeforeAnalyze");
            Dictionary<string, Dictionary<string, Tuple<List<IXLRow>, List<double>, List<IXLRange>>>> rowsDic = new Dictionary<string, Dictionary<string, Tuple<List<IXLRow>, List<double>, List<IXLRange>>>>();
            
            Logger.Info("Diff " + allFilePathList.Count.ToString() + "files");
            foreach(string path in allFilePathList)
            {
                Dictionary<string, Tuple<List<IXLRow>, List<double>, List<IXLRange>>> dic = new Dictionary<string, Tuple<List<IXLRow>, List<double>, List<IXLRange>>>();
                rowsDic.Add(path, dic);
                Logger.Info(path);
            }
            
            globalObject = rowsDic;
        }

        /// <summary>
        /// 分析一个sheet
        /// </summary>
        /// <param name="paramDic">传入的参数</param>
        /// <param name="sheet">被分析的sheet</param>
        /// <param name="result">存储当前文件的信息 ResultType { (String) FILEPATH [文件路径], (String) FILENAME [文件名], (String) MESSAGE [当查找时出现问题时输出的消息], (Object) RESULTOBJECT [用户自定的分析结果] }</param>
        /// <param name="globalObject">全局存在, 可以保存需要在其他调用时使用的数据, 如当前行号等</param>
        /// <param name="invokeCount">此分析函数被调用的次数</param>
        public void AnalyzeSheet(Dictionary<string, string> paramDic, IXLWorksheet sheet, ConcurrentDictionary<ResultType, Object> result, ref Object globalObject, int invokeCount)
        {
            Logger.Info("AnalyzeSheet" + invokeCount + ": " + sheet.Name + " in " + Path.GetFileName((string)result[ResultType.FILEPATH]));
            
            IXLRow lastUsedRow = sheet.LastRowUsed();
            int lastRowNum = lastUsedRow == null ? 0 : sheet.LastRowUsed().RowNumber();
            List<IXLRow> rows = new List<IXLRow>();
            List<IXLRange> mergeRangeList = new List<IXLRange>();
            int lastUsedColNum = 0;
            for(int i = 1; i <= lastRowNum; ++i)
            {
                IXLRow row = sheet.Row(i);
                rows.Add(row);
                
                IXLCell lastUsedCell = row.LastCellUsed();
                
                int rowLastCellUsedColumnNumber = lastUsedCell == null ? 0 : lastUsedCell.Address.ColumnNumber;
                if(lastUsedCell != null && lastUsedCell.IsMerged())
                {
                    rowLastCellUsedColumnNumber += lastUsedCell.MergedRange().ColumnCount() - 1;
                }
                
                IXLCells usedCells = row.CellsUsed();
                
                if(usedCells != null)
                {
                    foreach (IXLCell cell in usedCells)
                    {
                        if(cell.IsMerged())
                        {
                            IXLRange range = cell.MergedRange();
                            if(!mergeRangeList.Contains(range))
                            {
                                mergeRangeList.Add(range);
                            }
                        }
                    }
                }
                
                lastUsedColNum = rowLastCellUsedColumnNumber > lastUsedColNum ? rowLastCellUsedColumnNumber : lastUsedColNum;
            }
            
            List<double> widthList = new List<double>();
            for(int j = 1; j <= lastUsedColNum; ++j)
            {
                widthList.Add(sheet.Column(j).Width);
            }
            
            ((Dictionary<string, Dictionary<string, Tuple<List<IXLRow>, List<double>, List<IXLRange>>>>)globalObject)[(string)result[ResultType.FILEPATH]].Add(sheet.Name, new Tuple<List<IXLRow>, List<double>, List<IXLRange>>(rows, widthList, mergeRangeList));
        }

        /// <summary>
        /// 在所有输出前调用
        /// </summary>
        /// <param name="paramDic">传入的参数</param>
        /// <param name="workbook">用于输出的excel文件</param>
        /// <param name="globalObject">全局存在, 可以保存需要在其他调用时使用的数据, 如当前行号等</param>
        /// <param name="resultList">所有文件的信息</param>
        /// <param name="allFilePathList">分析的所有文件路径列表</param>
        public void RunBeforeSetResult(Dictionary<string, string> paramDic, XLWorkbook workbook, ref Object globalObject, ICollection<ConcurrentDictionary<ResultType, Object>> resultList, List<string> allFilePathList)
        {
            
        }

        /// <summary>
        /// 根据分析结果输出到excel中
        /// </summary>
        /// <param name="paramDic">传入的参数</param>
        /// <param name="workbook">用于输出的excel文件</param>
        /// <param name="result">存储当前文件的信息 ResultType { (String) FILEPATH [文件路径], (String) FILENAME [文件名], (String) MESSAGE [当查找时出现问题时输出的消息], (Object) RESULTOBJECT [用户自定的分析结果] }</param>
        /// <param name="globalObject">全局存在, 可以保存需要在其他调用时使用的数据, 如当前行号等</param>
        /// <param name="invokeCount">此输出函数被调用的次数</param>
        /// <param name="totalCount">总共需要调用的输出函数的次数</param>
        public void SetResult(Dictionary<string, string> paramDic, XLWorkbook workbook, ConcurrentDictionary<ResultType, Object> result, ref Object globalObject, int invokeCount, int totalCount)
        {
            
        }

        /// <summary>
        /// 所有调用结束后调用
        /// </summary>
        /// <param name="paramDic">传入的参数</param>
        /// <param name="workbook">用于输出的excel文件</param>
        /// <param name="globalObject">全局存在, 可以保存需要在其他调用时使用的数据, 如当前行号等</param>
        /// <param name="resultList">所有文件的信息</param>
        /// <param name="allFilePathList">分析的所有文件路径列表</param>
        public void RunEnd(Dictionary<string, string> paramDic, XLWorkbook workbook, ref Object globalObject, ICollection<ConcurrentDictionary<ResultType, Object>> resultList, List<string> allFilePathList)
        {
            Logger.Info("RunEnd");
            
            string zoom = "";
            if(paramDic.ContainsKey("zoom"))
            {
                zoom = paramDic["zoom"];
            }
            string freeze = "";
            if(paramDic.ContainsKey("freeze"))
            {
                freeze = paramDic["freeze"];
            }
            
            Dictionary<string, Dictionary<string, Tuple<List<IXLRow>, List<double>, List<IXLRange>>>> rowsDic = (Dictionary<string, Dictionary<string, Tuple<List<IXLRow>, List<double>, List<IXLRange>>>>)globalObject;
            
            List<string> analyzedFileNameList = new List<string>();
            
            int diffFileCount = 0;
            
            IXLWorksheet totalSheet = workbook.AddWorksheet("Res");
            int totalSheetLine = 0;
            
            foreach(string origPath in rowsDic.Keys)
            {
                string fileName = Path.GetFileName(origPath);
                if(analyzedFileNameList.Contains(fileName))
                {
                    continue;
                }
                ++diffFileCount;
                string revPath = "";
                foreach(string path in allFilePathList)
                {
                    if(Path.GetFileName(path).Equals(fileName))
                    {
                    revPath = path;
                    }
                }
                if(String.IsNullOrEmpty(revPath))
                {
                    continue;
                }
                
                ++totalSheetLine;
                totalSheet.Cell(totalSheetLine, 1).Value = totalSheetLine;
                totalSheet.Cell(totalSheetLine, 2).Value = fileName;
                
                Dictionary<string, Tuple<List<IXLRow>, List<double>, List<IXLRange>>> origSheetsRows = rowsDic[origPath];
                Dictionary<string, Tuple<List<IXLRow>, List<double>, List<IXLRange>>> revSheetsRows = rowsDic[revPath];
            
                analyzedFileNameList.Add(fileName);
                
                int leaveSize = 31 - "<> ".Length - diffFileCount.ToString().Length;
                string tempFileName = fileName.Replace("\\", "").Replace("/", "").Replace("?", "").Replace("*", "").Replace("[", "").Replace("]", "");
                if(tempFileName.Length > leaveSize)
                {
                    tempFileName = "..." + tempFileName.Substring(tempFileName.Length - (leaveSize - 3));
                }
                IXLWorksheet sheet = workbook.AddWorksheet("<" + diffFileCount + "> " + tempFileName);
                sheet.Cell(1, 1).Value = fileName;
                sheet.Cell(1, 2).Value = "+";
                sheet.Cell(1, 2).Style.Border.OutsideBorder = XLBorderStyleValues.Medium;
                sheet.Cell(1, 2).Style.Fill.BackgroundColor = XLColor.BlueGreen;
                sheet.Cell(1, 2).Style.Alignment.Horizontal = XLAlignmentHorizontalValues.Center;
                sheet.Cell(1, 3).Value = "-";
                sheet.Cell(1, 3).Style.Border.OutsideBorder = XLBorderStyleValues.Medium;
                sheet.Cell(1, 3).Style.Fill.BackgroundColor = XLColor.BlueGreen;
                sheet.Cell(1, 3).Style.Alignment.Horizontal = XLAlignmentHorizontalValues.Center;
                int sheetNowRow = 2;
                
                List<ConcurrentDictionary<ResultType, Object>> listTemp = new List<ConcurrentDictionary<ResultType, object>>();
                foreach (ConcurrentDictionary<ResultType, Object> res in resultList)
                {
                    listTemp.Add(res);
                }
                
                List<string> sheetNames = new List<string>();
                foreach (string name in origSheetsRows.Keys)
                {
                    if(!sheetNames.Contains(name))
                    {
                        sheetNames.Add(name);
                    }
                }
                foreach (string name in revSheetsRows.Keys)
                {
                    if(!sheetNames.Contains(name))
                    {
                        sheetNames.Add(name);
                    }
                }
                
                foreach(string name in sheetNames)
                {
                    Logger.Info("Diff: " + name + " in " + fileName + ", total: " + sheetNames.Count + "sheets");
                    
                    if(!origSheetsRows.ContainsKey(name) || !revSheetsRows.ContainsKey(name))
                    {
                        continue;
                    }
                    
                    int sheetAdd = 0;
                    int sheetDel = 0;
                    
                    sheet.Cell(sheetNowRow, 1).Value = name;
                    sheet.Cell(sheetNowRow, 1).Style.Border.OutsideBorder = XLBorderStyleValues.Medium;
                    sheet.Cell(sheetNowRow, 1).Style.Fill.BackgroundColor = XLColor.YellowProcess;
                    
                    List<string> origStrList = new  List<string>();
                    List<string> revStrList = new  List<string>();
                
                    List<IXLRow> origRows = null;
                    List<IXLRow> revRows = null;
                    if(origSheetsRows.ContainsKey(name))
                    {
                        origRows = origSheetsRows[name].Item1;
                    }
                    if(revSheetsRows.ContainsKey(name))
                    {
                        revRows = revSheetsRows[name].Item1;
                    }
                    
                    int origLastUsedColNum = origSheetsRows[name].Item2.Count;
                    if(origRows != null)
                    {
                        foreach(IXLRow row in origRows)
                        {
                            IXLCells cells = row.CellsUsed();
                            string value = "";
                            foreach(IXLCell cell in cells)
                            {
                                value += cell.CachedValue;
                            }
                            origStrList.Add(value);
                        }
                    }
                    int revLastUsedColNum = revSheetsRows[name].Item2.Count;
                    if(revRows != null)
                    {
                        foreach(IXLRow row in revRows)
                        {
                            IXLCells cells = row.CellsUsed();
                            string value = "";
                            foreach(IXLCell cell in cells)
                            {
                                value += cell.CachedValue;
                            }
                            
                            revStrList.Add(value);
                        }
                    }
                    
                    string diffSheetName = name;
                    int leaveSheetNameSize = 31 - ". ".Length - diffFileCount.ToString().Length;
                    if(diffSheetName.Length > leaveSheetNameSize)
                    {
                        diffSheetName = "..." + diffSheetName.Substring(leaveSheetNameSize - 3);
                    }
                    IXLWorksheet diffSheet = workbook.AddWorksheet(diffFileCount + ". " + diffSheetName);
                    
                    List<DiffRes> diffResList = DiffTool.Diff(origStrList, revStrList);
                    int nowOrigRow = 1;
                    int nowRevRow = 1;
                    int nowRow = 1;
                    
                    int origStartColNum = 3;
                    int revStartColNum = origLastUsedColNum + origStartColNum + 2;
                    int midColNum = origLastUsedColNum + origStartColNum;
                    
                    int lastDelStartRow = -1;
                    int lastAddRow = -1;
                    
                    int endColNum = revStartColNum + revLastUsedColNum - 1;
                    
                    int origRowNumColNum = 2;
                    int revRowNumColNum = midColNum + 1;
                    
                    diffSheet.Column(1).Width = 2;
                    diffSheet.Column(1).Style.Border.RightBorder = XLBorderStyleValues.Medium;
                    diffSheet.Column(1).Style.Alignment.Horizontal = XLAlignmentHorizontalValues.Center;
                    diffSheet.Column(1).Style.Alignment.Vertical = XLAlignmentVerticalValues.Center;
                    diffSheet.Column(midColNum).Width = 1;
                    diffSheet.Column(midColNum).Style.Border.LeftBorder = XLBorderStyleValues.Medium;
                    diffSheet.Column(midColNum).Style.Border.RightBorder = XLBorderStyleValues.Medium;
                    
                    bool isMod = false;
                    foreach(DiffRes diffRes in diffResList)
                    {
                        Logger.Info(diffRes.Type.ToString() + ": " + diffRes.Index);
                        if(diffRes.Type == DiffType.Delete)
                        {
                            ++sheetDel;
                        
                            lastAddRow = -1;
                            isMod = true;
                        
                            diffSheet.Cell(nowRow, 1).SetValue("-");
                            diffSheet.Cell(nowRow, 1).Style.Fill.BackgroundColor = XLColor.Pink;
                            
                            nowOrigRow = diffRes.Index;
                            
                            if(origRows.Count <= nowOrigRow)
                            {
                                continue;
                            }
                            IXLRow row = origRows[nowOrigRow];
                            int origStartColNumTemp = origStartColNum;
                            IXLCell lastCellUsed = row.LastCellUsed();
                            if(lastCellUsed != null)
                            {
                                int lastCellUsedColNum = lastCellUsed.Address.ColumnNumber;
                                for(int i = 1; i <= lastCellUsedColNum; ++i)
                                {
                                    IXLCell cell = row.Cell(i);
                                    if(cell.HasFormula)
                                    {
                                        diffSheet.Cell(nowRow, origStartColNumTemp).SetValue(cell.CachedValue);
                                    }
                                    else
                                    {
                                        cell.CopyTo(diffSheet.Cell(nowRow, origStartColNumTemp));
                                    }
                                    if(diffSheet.Row(nowRow).Height < row.Height)
                                    {
                                        diffSheet.Row(nowRow).Height = row.Height;
                                    }
                                    ++origStartColNumTemp;
                                }
                            }
                            ++nowOrigRow;
                            
                            if(lastDelStartRow == -1)
                            {
                                lastDelStartRow = nowRow;
                            }
                        }
                        else if(diffRes.Type == DiffType.Add)
                        {
                            ++sheetAdd;
                            
                            int nowRowTemp = -1; 
                            if(lastDelStartRow != -1)
                            {
                                nowRowTemp = lastDelStartRow;
                                --nowRow;
                            }
                            else if(lastAddRow != -1)
                            {
                                nowRowTemp = lastAddRow + 1;
                            }
                            else
                            {
                                nowRowTemp = nowRow;
                            }
                            nowRow = nowRowTemp > nowRow ? nowRowTemp : nowRow;
                            lastDelStartRow = -1;
                            lastAddRow = nowRowTemp;
                            
                            if(isMod)
                            {
                                diffSheet.Cell(nowRowTemp, 1).SetValue("~");
                                diffSheet.Cell(nowRowTemp, 1).Style.Fill.BackgroundColor = XLColor.YellowProcess;
                            }
                            else
                            {
                                diffSheet.Cell(nowRowTemp, 1).SetValue("+");
                                diffSheet.Cell(nowRowTemp, 1).Style.Fill.BackgroundColor = XLColor.BlueGreen;
                            }
                            nowRevRow = diffRes.Index;
                            
                            if(revRows.Count <= nowRevRow)
                            {
                                continue;
                            }
                            IXLRow row = revRows[nowRevRow];
                            int revStartColNumTemp = revStartColNum;
                            IXLCell lastCellUsed = row.LastCellUsed();
                            if(lastCellUsed != null)
                            {
                                int lastCellUsedColNum = lastCellUsed.Address.ColumnNumber;
                                for(int i = 1; i <= lastCellUsedColNum; ++i)
                                {
                                    IXLCell cell = row.Cell(i);
                                    if(cell.HasFormula)
                                    {
                                        diffSheet.Cell(nowRowTemp, revStartColNumTemp).SetValue(cell.CachedValue);
                                    }
                                    else
                                    {
                                        cell.CopyTo(diffSheet.Cell(nowRowTemp, revStartColNumTemp));
                                    }
                                    if(diffSheet.Row(nowRowTemp).Height < row.Height)
                                    {
                                        diffSheet.Row(nowRowTemp).Height = row.Height;
                                    }
                                    ++revStartColNumTemp;
                                }
                            }
                            ++nowRevRow;
                        }
                        else if(diffRes.Type == DiffType.None)
                        {
                            lastAddRow = -1;
                            lastDelStartRow = -1;
                            isMod = false;
                            diffSheet.Cell(nowRow, 1).SetValue("");
                            nowOrigRow = diffRes.Index;
                            
                            if(origRows.Count <= nowOrigRow)
                            {
                                continue;
                            }
                            IXLRow row = origRows[nowOrigRow];
                            int origStartColNumTemp = origStartColNum;
                            int revStartColNumTemp = revStartColNum;
                            diffSheet.Cell(nowRow, 1).SetValue("=");
                            IXLCell lastCellUsed = row.LastCellUsed();
                            if(lastCellUsed != null)
                            {
                                int lastCellUsedColNum = lastCellUsed.Address.ColumnNumber;
                                for(int i = 1; i <= lastCellUsedColNum; ++i)
                                {
                                    IXLCell cell = row.Cell(i);
                                    if(cell.HasFormula)
                                    {
                                        diffSheet.Cell(nowRow, origStartColNumTemp).SetValue(cell.CachedValue);
                                    }
                                    else
                                    {
                                        cell.CopyTo(diffSheet.Cell(nowRow, origStartColNumTemp));
                                    }
                                    if(cell.HasFormula)
                                    {
                                        diffSheet.Cell(nowRow, revStartColNumTemp).SetValue(cell.CachedValue);
                                    }
                                    else
                                    {
                                        cell.CopyTo(diffSheet.Cell(nowRow, revStartColNumTemp));
                                    }
                                    if(diffSheet.Row(nowRow).Height < row.Height)
                                    {
                                        diffSheet.Row(nowRow).Height = row.Height;
                                    }
                                    ++origStartColNumTemp;
                                    ++revStartColNumTemp;
                                }
                            }
                            ++nowOrigRow;
                            ++nowRevRow;
                        }
                        
                        ++nowRow;
                    }
                    
                    // 补救措施
                    List<IXLRange> origMergedRangeList = origSheetsRows[name].Item3;
                    List<IXLRange> revMergedRangeList = revSheetsRows[name].Item3;
                    Dictionary<int, int> origRowNumDic = new Dictionary<int, int>();
                    Dictionary<int, int> revRowNumDic = new Dictionary<int, int>();
                    int nowOrigRowForMerge = 1;
                    int nowRevRowForMerge = 1;
                    IXLRow lastUsedRow = diffSheet.LastRowUsed();
                    int lastNum = lastUsedRow == null ? 0 : diffSheet.LastRowUsed().RowNumber();
                    for(int i = 1; i <= lastNum; ++i)
                    {
                        if(diffSheet.Cell(i, 1).CachedValue.Equals(""))
                        {
                            diffSheet.Row(i).Delete();
                            --i;
                            --lastNum;
                        }
                        else if(diffSheet.Cell(i, 1).CachedValue.Equals("~"))
                        {
                            diffSheet.Range(i, 1, i, endColNum).Style.Border.OutsideBorder = XLBorderStyleValues.MediumDashDot;
                            diffSheet.Range(i, 1, i, endColNum).Style.Border.OutsideBorderColor = XLColor.YellowProcess;
                            
                            diffSheet.Cell(i, origRowNumColNum).Value = nowOrigRowForMerge;
                            origRowNumDic.Add(nowOrigRowForMerge, i);
                            ++nowOrigRowForMerge;
                            diffSheet.Cell(i, revRowNumColNum).Value = nowRevRowForMerge;
                            revRowNumDic.Add(nowRevRowForMerge, i);
                            ++nowRevRowForMerge;
                        }
                        else if(diffSheet.Cell(i, 1).CachedValue.Equals("+"))
                        {
                            diffSheet.Range(i, 1, i, endColNum).Style.Border.OutsideBorder = XLBorderStyleValues.MediumDashDot;
                            diffSheet.Range(i, 1, i, endColNum).Style.Border.OutsideBorderColor = XLColor.BlueGreen;
                            diffSheet.Cell(i, revRowNumColNum).Value = nowRevRowForMerge;
                            revRowNumDic.Add(nowRevRowForMerge, i);
                            ++nowRevRowForMerge;
                        }
                        else if(diffSheet.Cell(i, 1).CachedValue.Equals("-"))
                        {
                            diffSheet.Range(i, 1, i, endColNum).Style.Border.OutsideBorder = XLBorderStyleValues.MediumDashDot;
                            diffSheet.Range(i, 1, i, endColNum).Style.Border.OutsideBorderColor = XLColor.Pink;
                            diffSheet.Cell(i, origRowNumColNum).Value = nowOrigRowForMerge;
                            origRowNumDic.Add(nowOrigRowForMerge, i);
                            ++nowOrigRowForMerge;
                        }
                        else if(diffSheet.Cell(i, 1).CachedValue.Equals("="))
                        {
                            diffSheet.Cell(i, origRowNumColNum).Value = nowOrigRowForMerge;
                            origRowNumDic.Add(nowOrigRowForMerge, i);
                            ++nowOrigRowForMerge;
                            diffSheet.Cell(i, revRowNumColNum).Value = nowRevRowForMerge;
                            revRowNumDic.Add(nowRevRowForMerge, i);
                            ++nowRevRowForMerge;
                        }
                    }
                    diffSheet.Column(origRowNumColNum).AdjustToContents();
                    diffSheet.Column(origRowNumColNum).Style.Fill.SetBackgroundColor(XLColor.AliceBlue);
                    diffSheet.Column(origRowNumColNum).Style.Alignment.Horizontal = XLAlignmentHorizontalValues.Center;
                    diffSheet.Column(origRowNumColNum).Style.Alignment.Vertical = XLAlignmentVerticalValues.Center;
                    diffSheet.Column(revRowNumColNum).AdjustToContents();
                    diffSheet.Column(revRowNumColNum).Style.Fill.SetBackgroundColor(XLColor.AliceBlue);
                    diffSheet.Column(revRowNumColNum).Style.Alignment.Horizontal = XLAlignmentHorizontalValues.Center;
                    diffSheet.Column(revRowNumColNum).Style.Alignment.Vertical = XLAlignmentVerticalValues.Center;
                    foreach(IXLRange range in origMergedRangeList)
                    {
                        IXLAddress firstAddress = range.RangeAddress.FirstAddress;
                        IXLAddress lastAddress = range.RangeAddress.LastAddress;
                        if(origRowNumDic.ContainsKey(firstAddress.RowNumber) && origRowNumDic.ContainsKey(lastAddress.RowNumber))
                        {
                            diffSheet.Range(origRowNumDic[firstAddress.RowNumber], firstAddress.ColumnNumber + origStartColNum - 1, origRowNumDic[lastAddress.RowNumber], lastAddress.ColumnNumber + origStartColNum - 1).Merge();
                        }
                    }
                    foreach(IXLRange range in revMergedRangeList)
                    {
                        IXLAddress firstAddress = range.RangeAddress.FirstAddress;
                        IXLAddress lastAddress = range.RangeAddress.LastAddress;
                        if(revRowNumDic.ContainsKey(firstAddress.RowNumber) && revRowNumDic.ContainsKey(lastAddress.RowNumber))
                        {
                            diffSheet.Range(revRowNumDic[firstAddress.RowNumber], firstAddress.ColumnNumber + revStartColNum - 1, revRowNumDic[lastAddress.RowNumber], lastAddress.ColumnNumber + revStartColNum - 1).Merge();
                        }
                    }
                    
                    List<double> origWidthList =  origSheetsRows[name].Item2;
                    List<double> revWidthList =  revSheetsRows[name].Item2;
                    int index = 0;
                    for(int i = origStartColNum; i < origStartColNum + origWidthList.Count; ++i)
                    {
                        diffSheet.Column(i).Width = origWidthList[index];
                        ++index;
                    }
                    index = 0;
                    for(int i = revStartColNum; i < revStartColNum + revWidthList.Count; ++i)
                    {
                        diffSheet.Column(i).Width = revWidthList[index];
                        ++index;
                    }
                    
                    if(!String.IsNullOrEmpty(zoom))
                    {
                        diffSheet.SheetView.ZoomScale = int.Parse(zoom);
                    }
                    if(!String.IsNullOrEmpty(freeze) && freeze.Equals("true"))
                    {
                        diffSheet.SheetView.FreezeColumns(midColNum);
                    }
                    
                    sheet.Cell(sheetNowRow, 2).Value = sheetAdd;
                    sheet.Cell(sheetNowRow, 2).Style.Alignment.Horizontal = XLAlignmentHorizontalValues.Left;
                    sheet.Cell(sheetNowRow, 2).Style.Border.OutsideBorder = XLBorderStyleValues.Medium;
                    sheet.Cell(sheetNowRow, 3).Value = sheetDel;
                    sheet.Cell(sheetNowRow, 3).Style.Alignment.Horizontal = XLAlignmentHorizontalValues.Left;
                    sheet.Cell(sheetNowRow, 3).Style.Border.OutsideBorder = XLBorderStyleValues.Medium;
                    ++sheetNowRow;
                }
                
                IXLColumns colUsedRes = sheet.ColumnsUsed();
                foreach(IXLColumn col in colUsedRes)
                {
                    col.AdjustToContents(1, 3);
                }
                sheet.Column(1).InsertColumnsBefore(1);
                sheet.Row(1).InsertRowsAbove(1);
    
            }
        }
    }
